document.addEventListener('DOMContentLoaded', function() {
    var slideInOpen = document.getElementById('slide-in-open');
    var slideInMenu = document.querySelector('.slide-in-menu-container');

    // Toggle mobile menu
    if (slideInOpen && slideInMenu) {
        slideInOpen.addEventListener('click', function() {
            slideInOpen.classList.toggle('open');
            slideInMenu.classList.toggle('slide-in-menu');
            slideInMenu.style.right = slideInMenu.style.right === '0px' ? '-100%' : '0px'; // Slide in/out
        });
    }

    // Handle submenu toggles
    var menuItemsWithChildren = document.querySelectorAll('.mobile-slideout-menu .menu-item-has-children > a');
    menuItemsWithChildren.forEach(function(item) {
        item.addEventListener('click', function(e) {
            e.preventDefault(); // Prevent default link behavior
            var subMenu = this.nextElementSibling; // Get the submenu
            if (subMenu && subMenu.classList.contains('sub-menu')) {
                subMenu.style.display = subMenu.style.display === 'block' ? 'none' : 'block'; // Toggle display
                this.classList.toggle('submenu-opened'); // Toggle class for styling
            }
        });
    });
});

console.log('Menu button clicked'); // Inside the click event for slideInOpen
console.log('Submenu toggled'); // Inside the submenu toggle function